data.schema:
{
  "type" : "object",
  "id" : "urn:jsonschema:io:gravitee:policy:apikey:configuration:ApiKeyPolicyConfiguration",
  "properties" : {
    "propagateApiKey" : {
      "title": "Propagate API Key to upstream API",
      "type" : "boolean"
    }
  }
}

{
  "type": "object",
  "id": "urn:jsonschema:io:gravitee:policy:assignattributes:configuration:AssignAttributesPolicyConfiguration",
  "properties": {
    "scope" : {
      "title": "Scope",
      "description": "Execute policy on <strong>request</strong> or <strong>response</strong> phase.",
      "type" : "string",
      "default": "REQUEST",
      "enum" : [ "REQUEST", "RESPONSE", "REQUEST_CONTENT", "RESPONSE_CONTENT" ],
      "deprecated": true
    },
    "attributes" : {
      "type" : "array",
      "title": "Assign context attributes",
      "items" : {
        "type" : "object",
        "id" : "urn:jsonschema:io:gravitee:policy:assignattributes:configuration:Attribute",
        "title": "Attribute",
        "properties" : {
          "name" : {
            "title": "Name",
            "description": "Name of the attribute",
            "type" : "string"
          },
          "value" : {
            "title": "Value",
            "description": "Value of the attribute (Support EL)",
            "type" : "string",
            "x-schema-form": {
              "expression-language": true
            }
          }
        },
        "required": [
          "name",
          "value"
        ]
      }
    }
  }
}

{
  "type" : "object",
  "id" : "urn:jsonschema:io:gravitee:policy:assigncontent:configuration:AssignContentPolicyConfiguration",
  "properties" : {
    "scope" : {
      "title": "Scope",
      "description": "Execute policy on <strong>request</strong> or <strong>response</strong> phase.",
      "type" : "string",
      "default": "REQUEST",
      "enum" : [ "REQUEST", "RESPONSE" ],
      "deprecated": "true"
    },
    "body" : {
      "title": "Body content",
      "description": "The body content to attach to the request or to the response. You can also make use of freemarker templating engine to map an incoming body content to a new one.",
      "type" : "string",
      "x-schema-form": {
        "type": "codemirror",
        "codemirrorOptions": {
          "placeholder": "Put the body content here",
          "lineWrapping": true,
          "lineNumbers": true,
          "allowDropFileTypes": true,
          "autoCloseTags": true,
          "mode": "xml"
        }
      }
    }
  },
  "required": [
    "scope",
    "body"
  ]
}

{
  "type": "object",
  "id": "urn:jsonschema:io:gravitee:policy:cache:configuration:CachePolicyConfiguration",
  "properties": {
    "cacheName": {
      "title": "Cache name",
      "description": "The cache resource used to store the element.",
      "type": "string",
      "x-schema-form": {
        "event": {
          "name": "fetch-resources",
          "regexTypes": "^cache"
        }
      }
    },
    "key": {
      "title": "Key",
      "description": "The key used to store the element (Support EL).",
      "type": "string",
      "x-schema-form": {
        "expression-language": true
      }
    },
    "timeToLiveSeconds": {
      "title": "Time to live (in seconds)",
      "default": 600,
      "description": "Time to live of the element put in cache (Default to 10 minutes).",
      "type": "integer"
    },
    "useResponseCacheHeaders": {
      "title": "Use response headers",
      "description": "Time to live based on 'Cache-Control' and / or 'Expires' headers from response.",
      "type": "boolean"
    },
    "scope": {
      "title": "Scope",
      "description": "Cached response can be set for a single consumer (application) or for all consumers.<br><strong>WARNING:</strong> Please be aware that by using an \"API\" scope, data will be shared between all consumers!",
      "type": "string",
      "default": "APPLICATION",
      "enum": [
        "API",
        "APPLICATION"
      ]
    }
  },
  "required": [
    "cacheName",
    "timeToLiveSeconds"
  ]
}

{
  "type" : "object",
  "id" : "urn:jsonschema:io:gravitee:policy:dynamicrouting:configuration:DynamicRoutingPolicyConfiguration",
  "title": "Dynamic Routing",
  "description": "Route request to an other endpoint according to specified rules",
  "properties" : {
    "rules" : {
      "title": "Routing rules",
      "description": "Ordered list of rules to apply to inbound request.",
      "type" : "array",
      "items" : {
        "type" : "object",
        "title": "Rule",
        "id" : "urn:jsonschema:io:gravitee:policy:dynamicrouting:configuration:Rule",
        "properties" : {
          "pattern" : {
            "title": "Match expression",
            "description": "The key used to store the element (Support EL).",
            "type" : "string",
            "x-schema-form": {
              "expression-language": true
            }
          },
          "url" : {
            "title": "Redirect to",
            "description": "The target endpoint (Support EL).",
            "type" : "string",
            "x-schema-form": {
              "expression-language": true
            }
          }
        },
        "required": [
          "pattern",
          "url"
        ]
      }
    }
  },
  "required": [
    "rules"
  ]
}

{
  "type": "object",
  "id": "urn:jsonschema:io:gravitee:policy:generatehttpsignature:configuration:GenerateHttpSignaturePolicyConfiguration",
  "properties": {
    "scheme" : {
      "title": "Scheme",
      "description": "Signature Scheme",
      "type" : "string",
      "default": "AUTHORIZATION",
      "enum" : [
        "AUTHORIZATION",
        "SIGNATURE"
      ],
      "x-schema-form": {
        "type": "select",
        "titleMap": {
          "AUTHORIZATION": "\"Signature\" HTTP Authentication Scheme",
          "SIGNATURE": "\"Signature\" HTTP Header"
        }
      }
    },
    "keyId": {
      "title": "Key ID",
      "description": "The key ID used to sign (support EL).",
      "type": "string"
    },
    "secret": {
      "title": "Secret",
      "description": "The secret key used to sign (support EL).",
      "type": "string"
    },
    "algorithm" : {
      "title": "Algorithm",
      "description": "The HMAC digest algorithm.",
      "type" : "string",
      "default": "HMAC_SHA256",
      "enum" : [
        "HMAC_SHA1",
        "HMAC_SHA256",
        "HMAC_SHA384",
        "HMAC_SHA512"
      ]
    },
    "headers" : {
      "type": "array",
      "title": "Headers",
      "description": "List of headers to use for HTTP signature creation.",
      "items": {
        "type": "string",
        "title": "Header"
      }
    },
    "created": {
      "title": "Include the created timestamp in the signature",
      "default": true,
      "description": "Add (created) header in the signature. (Highly recommended)",
      "type": "boolean"
    },
    "expires": {
      "title": "Include the expires timestamp in the signature",
      "default": true,
      "description": "Add (expires) header in the signature. (Highly recommended)",
      "type": "boolean"
    },
    "validityDuration": {
      "title": "Validity duration",
      "default": 3,
      "description": "Signature's maximum validation duration in seconds",
      "type": "integer",
      "minimum": 1,
      "x-schema-form": {
        "hidden": [
          {
            "$eq": {
              "expires": false
            }
          }
        ]
      }
    }
  },
  "required": [
    "scheme",
    "secret",
    "keyId",
    "validityDuration",
    "algorithm"
  ]
}

{
  "type" : "object",
  "id" : "urn:jsonschema:io:gravitee:policy:generatejwt:configuration:GenerateJwtPolicyConfiguration",
  "properties" : {
    "signature": {
      "title": "Signature",
      "description": "Define how the JSON Web Token must be signed.",
      "type" : "string",
      "default": "RSA_RS256",
      "enum" : [
        "RSA_RS256",
        "HMAC_HS256",
        "HMAC_HS384",
        "HMAC_HS512"
      ],
      "x-schema-form": {
        "type": "select",
        "titleMap": {
          "RSA_RS256": "RS256 - RSA signature with SHA-256",
          "HMAC_HS256": "HS256 - HMAC with SHA-256, requires 256+ bit secret",
          "HMAC_HS384": "HS384 - HMAC with SHA-384, requires 384+ bit secret",
          "HMAC_HS512": "HS512 - HMAC with SHA-512, requires 512+ bit secret"
        }
      }
    },
    "keyResolver": {
      "title": "Key resolver",
      "description": "Define how the private key / secret key must be resolved.",
      "type" : "string",
      "default": "INLINE",
      "enum" : [
        "INLINE",
        "PLAIN",
        "JKS",
        "PKCS12"
      ],
      "x-schema-form": {
        "type": "select",
        "titleMap": {
          "INLINE": "The content of the private key / secret key is provided inline",
          "PEM": "The content of the private key is provided by reading a PEM file from filesystem (only for RS algorithms)",
          "JKS": "The content of the private key is provided by reading a JKS file from filesystem (only for RS algorithms)",
          "PKCS12": "The content of the private key is provided by reading a PKCS12 file from filesystem (only for RS algorithms)"
        }
      }
    },
    "alias" : {
      "type" : "string",
      "title": "Key alias",
      "description": "Alias used to access a keystore entry (only for JKS and PKCS#12)"
    },
    "storepass" : {
      "type" : "string",
      "title": "Store password",
      "description": "Pass used to access the key store (only for JKS and PKCS#12)"
    },
    "keypass" : {
      "type" : "string",
      "title": "Key password",
      "description": "Pass used to access the particular key pair's private key (only for JKS)"
    },
    "kid" : {
      "type" : "string",
      "title": "Key ID",
      "description": "Specifies the key ID (kid) to include in the JWT header"
    },
    "x509CertificateChain": {
      "title": "Certificate chain",
      "description": "Whether a certificate chain should be added as an X5C attribute (only for RS256, JKS and PKCS#12)",
      "type" : "string",
      "default": "NONE",
      "enum" : [
        "NONE",
        "X5C"
      ],
      "x-schema-form": {
        "type": "select",
        "titleMap": {
          "NONE": "Do not add a certificate chain",
          "X5C": "Add a certificate chain as an X5C attribute"
        }
      }
    },
    "id" : {
      "type" : "string",
      "title": "JWT ID. Supports EL.",
      "description": "JWT ID (jti) claim is a unique identifier for the JWT. Defaults to UUID.",
      "x-schema-form": {
        "expression-language": true
      }
    },
    "audiences" : {
      "type" : "array",
      "title": "Audiences",
      "description": "Claim identifies the recipients that the JWT is intended for. Supports EL.",
      "items" : {
        "type" : "string"
      }
    },
    "issuer" : {
      "type" : "string",
      "title": "Issuer",
      "description": "Claim that identifies the issuer of the JWT. Supports EL.",
      "default": "urn://gravitee-api-gw",
      "x-schema-form": {
        "expression-language": true
      }
    },
    "expiresIn" : {
      "type" : "integer",
      "title": "Expires in",
      "description": "Lifespan of the JWT",
      "default": 30
    },
    "expiresInUnit" : {
      "type" : "string",
      "title": "Expires in unit",
      "description": "Lifespan of the JWT in seconds, minutes, hours, or days.",
      "enum" : [ "SECONDS", "MINUTES", "HOURS", "DAYS" ],
      "default": "SECONDS"
    },
    "subject" : {
      "type" : "string",
      "title": "Subject",
      "description": "Claim identifies or makes a statement about the subject of the JWT. Supports EL.",
      "x-schema-form": {
        "expression-language": true
      }
    },
    "content" : {
      "type" : "string",
      "title": "Private key / Secret key / key store path",
      "description": "Specifies a PEM-encoded private key used to sign the JWT or the secret key used to sign tokens with an HMAC algorithm, or the path to your key store",
      "x-schema-form": {
        "type": "codemirror",
        "codemirrorOptions": {
          "placeholder": "Put PEM-encoded private key or the secret key or the path to your key store",
          "lineWrapping": true,
          "lineNumbers": true,
          "allowDropFileTypes": true
        }
      }
    },
    "customClaims" : {
      "type" : "array",
      "title": "Custom claims",
      "description": "Specify additional claim name/value pair(s) in the payload of the JWT",
      "items" : {
        "type" : "object",
        "title": "Claim",
        "id" : "urn:jsonschema:io:gravitee:policy:generatejwt:model:Claim",
        "properties" : {
          "name" : {
            "type" : "string",
            "title": "Name",
            "description": "Name of the claim"
          },
          "value" : {
            "type" : "string",
            "title": "Value",
            "description": "Value of the claim. Supports EL.",
            "x-schema-form": {
              "expression-language": true
            }
          }
        },
        "required": [
          "name",
          "value"
        ]
      }
    }
  },
  "required": [
    "signature",
    "keyResolver",
    "content"
  ]
}

