        "type": "codemirror",
        "codemirrorOptions": {
          "placeholder": "Put your groovy script here or drag'n'drop groovy script",
          "lineWrapping": true,
          "lineNumbers": true,
          "allowDropFileTypes": true,
          "autoCloseTags": true,
