{
  "type": "object",
  "id": "urn:jsonschema:io:gravitee:policy:callout:configuration:CalloutHttpPolicyConfiguration",
  "properties": {
    "scope": {
      "title": "Scope",
      "description": "Execute policy on <strong>request</strong> (HEAD) phase, <strong>response</strong> (HEAD) phase, <strong>request_content</strong> (includes payload) phase, <strong>response content</strong> (includes payload) phase.",
      "type": "string",
      "default": "REQUEST",
      "enum": [
        "REQUEST",
        "RESPONSE",
        "REQUEST_CONTENT",
        "RESPONSE_CONTENT"
      ],
      "deprecated": true
    },
    "method": {
      "title": "HTTP Method",
      "description": "HTTP method to invoke the endpoint.",
      "type": "string",
      "default": "GET",
      "enum": [
        "GET",
        "POST",
        "PUT",
        "DELETE",
        "PATCH",
        "HEAD",
        "CONNECT",
        "OPTIONS",
        "TRACE"
      ]
    },
    "useSystemProxy": {
      "title": "Use system proxy",
      "description": "Use the system proxy configured by your administrator.",
      "type": "boolean"
    },
    "url": {
      "title": "URL",
      "type": "string"
    },
    "headers": {
      "type": "array",
      "title": "Request Headers",
      "items": {
        "type": "object",
        "id": "urn:jsonschema:io:gravitee:policy:callout:configuration:HttpHeader",
        "title": "Header",
        "properties": {
          "name": {
            "title": "Name",
            "type": "string"
          },
          "value": {
            "title": "Value",
            "type": "string"
          }
        }
      },
      "required": [
        "name",
        "value"
      ]
    },
    "body": {
      "title": "Request body",
      "type": "string",
      "x-schema-form": {
        "type": "codemirror",
        "codemirrorOptions": {
          "placeholder": "Put request body here",
          "lineWrapping": true,
          "lineNumbers": true,
          "allowDropFileTypes": true,
          "autoCloseTags": true
        }
      }
    },
    "fireAndForget": {
      "title": "Fire & forget",
      "description": "Make the http call without expecting any response. When activating this mode, context variables and exit on error are useless.",
      "type": "boolean",
      "default": false
    },
    "variables": {
      "type": "array",
      "title": "Context variables",
      "items": {
        "type": "object",
        "id": "urn:jsonschema:io:gravitee:policy:callout:configuration:Variable",
        "title": "Variable",
        "properties": {
          "name": {
            "title": "Name",
            "type": "string"
          },
          "value": {
            "title": "Value",
            "type": "string",
            "default": "{#jsonPath(#calloutResponse.content, '$.field')}"
          }
        }
      },
      "required": [
        "name",
        "value"
      ],
      "x-schema-form": {
        "hidden": [
          {
            "$eq": {
              "fireAndForget": true
            }
          }
        ]
      }
    },
    "exitOnError": {
      "title": "Exit on error",
      "description": "Terminate the request if the error condition is true",
      "type": "boolean",
      "default": false,
      "x-schema-form": {
        "hidden": [
          {
            "$eq": {
              "fireAndForget": true
            }
          }
        ]
      }
    },
    "errorCondition": {
      "title": "Error condition",
      "description": "The condition which will be verified to end the request (support EL).",
      "default": "{#calloutResponse.status >= 400 and #calloutResponse.status <= 599}",
      "type": "string",
      "x-schema-form": {
        "expression-language": true,
        "hidden": [
          {
            "$eq": {
              "fireAndForget": true,
              "exitOnError": false
            }
          }
        ]
      }
    },
    "errorStatusCode": {
      "title": "Error status code",
      "description": "HTTP Status Code send to the consumer if the condition is true",
      "type": "string",
      "default": "500",
      "enum": [
        "100",
        "101",
        "102",
        "200",
        "201",
        "202",
        "203",
        "204",
        "205",
        "206",
        "207",
        "300",
        "301",
        "302",
        "303",
        "304",
        "305",
        "307",
        "400",
        "401",
        "402",
        "403",
        "404",
        "405",
        "406",
        "407",
        "408",
        "409",
        "410",
        "411",
        "412",
        "413",
        "414",
        "415",
        "416",
        "417",
        "422",
        "423",
        "424",
        "429",
        "500",
        "501",
        "502",
        "503",
